import React from "react";
import { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";

const Home = () => {
  const [data, setData] = useState(null);
 const [loading, setLoading] = useState(true);
 const [error, setError] = useState(null);

 useEffect(() => {
  fetchData();
}, []);

const fetchData = async () => {
  try {
    const response = await fetch("https://m1smj96xd3.execute-api.ca-central-1.amazonaws.com/dev/courses");
    if (!response.ok) {
      throw new Error("Network response was not ok.");
    }
    const data = await response.json();
    setData(data);
    setLoading(false);
  } catch (error) {
    console.error("Error fetching data:", error);
    setLoading(false);
  }
};

    return (
      <main>
        <div className="sidebar">
          <ul>
            <li>
              <a href="#">Home</a>
            </li>
            <li>
              <a href="#">My Topics</a>
            </li>
            <li>
              <a href="#">Settings</a>
            </li>
          </ul>
        </div>

        <h2 className="trending-topics">Trending Topics</h2>

        <div className="box-container">
        {data &&
          data['courses'].map(({ name, courseImage, videoUrl }) => (
            <div className="box" key={name}>
              <NavLink to={{
   pathname:"/coursecontent?title="+name,
   state: {title:'from home page'}  
 }} className="box-link">
              <h2>{name}</h2>

              <div className="image-container">
                <img src={courseImage} alt="first topic" />
              </div>
              </NavLink>
          </div>
          ))}
          
        </div>
      </main>
    );
}

export default Home;
