import React, { useState } from "react";
import { Auth } from 'aws-amplify';
import { NavLink, useNavigate } from 'react-router-dom';
import { Link, Redirect } from 'react-router-dom';


function Signup() {
    const [firstname, setFirstname] = useState('');
    const [lastname, setLastname] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const navigate = useNavigate();

    const handleFirstnameChange = (event) => {
        setFirstname(event.target.value);
    };

    const handleLastnameChange = (event) => {
        setLastname(event.target.value);
    };

    const handleEmailChange = (event) => {
        setEmail(event.target.value);
    };

    const handlePhoneChange = (event) => {
        setPhone(event.target.value);
    };

    const handlePasswordChange = (event) => {
        setPassword(event.target.value);
    };

    const handleConfirmPasswordChange = (event) => {
        setConfirmPassword(event.target.value);
    };

    const handleLogin = (firstname, lastname, email, phonenumber, password, confirmpassword ) => {
        Auth.signIn(email, password)
            .then(user => {
                // localStorage.setItem(AUTH_USER_TOKEN_KEY, user.signInUserSession.accessToken.jwtToken);
                console.log(JSON.stringify(user));
                console.log("Signup Successful");

                navigate('/');

            })
            .catch(err => {
                console.log("Signup Failed");

                console.log(err);
                navigate('/signup');
            });
    };
    return (
        <div class="container">
            <h2>Sign Up</h2>

            <div>
                <label for="firstname">Firstname:</label>
                <input type="firstname" id="firstname" name="firstname" required/>
            </div>

            <div>
                <label for="lastname">Lastname:</label>
                <input type="lastname" id="lastname" name="lastname" required/>
            </div>

            <div>
                <label for="email">Email:</label>
                <input type="email" id="email" name="email" required/>
            </div>

            <div>
                <label for="phone">Phone Number:</label>
                <input type="tel" id="phone" name="phone" pattern="[0-9]{10}" placeholder="Enter a 10-digit phone number" required />
            </div>


            <div>
                <label for="password">Password:</label>
                <input type="password" id="password" name="password" required />
            </div>

            <div>
                <label for="confirm password">Confirm Password:</label>
                <input type="password" id="password" name="password" required />
            </div>

            <div class="option">
                <input type="submit" value="Sign Up" />
                <input type="submit" value="Cancel" />
            </div>
        </div>
    );
}


export default Signup;
