import React, { useState } from "react";
import { Auth } from 'aws-amplify';
import { Link, Redirect } from 'react-router-dom';

import FormContainer from '../../Components/Styled/FormWrapper';
import { colors } from '../../Themes/Colors';

function ForgotPasswordPage() {
  const [username, setUsername] = useState('');
  const [redirect, setRedirect] = useState(false);
  const [loading, setLoading] = useState(false);

  const handleSubmit = (event) => {
    event.preventDefault();

    setLoading(true);

    Auth.forgotPassword(username)
      .then(data => {
        notification.success({
          message: 'Redirecting you in a few!',
          description: 'Account confirmed successfully!',
          placement: 'topRight',
          duration: 1.5,
          onClose: () => {
            setRedirect(true);
          }
        });
      })
      .catch(err => {
        notification.error({
          message: 'User confirmation failed',
          description: err.message,
          placement: 'topRight',
          duration: 1.5
        });
        setLoading(false);
      });
  };

  return (
    <React.Fragment>
      <Typography.Title style={{ color: 'white' }} className="header">SLP- Structured Learning Platform</Typography.Title>
      <FormContainer onSubmit={handleSubmit} className="login-form">
        <Form.Item>
          <Input 
            prefix={<Icon type="user" style={{ color: colors.transparentBlack }} />} 
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </Form.Item>
        <Form.Item className="text-center">
          <Row>
            <Col lg={24}>
              <Button 
                style={{ width: '100%', backgroundColor: '#4c4456', border: '0px', color: 'white'}}
                type="primary" 
                htmlType="submit" 
                className="login-form-button"
                loading={loading}
              >
                {loading ? <Spin indicator={<Icon type="loading" style={{ fontSize: 24 }} spin />} /> : 'Confirm username'}
              </Button>
            </Col>
            <Col lg={24}>
              <Link to="/login" className="custom-link" style={{ color: 'black' }}>Ooh! Wait! I've remembered!</Link>
            </Col>
          </Row>
        </Form.Item>
      </FormContainer>
      {redirect && (
        <Redirect
          to={{
            pathname: '/reset-password',
            search: `?username=${username}`
          }}
        />
      )}
    </React.Fragment>
  );
}

export default ForgotPasswordPage;
