import React, { useState } from "react";
import { Auth } from 'aws-amplify';
import { useNavigate } from 'react-router-dom';

const handleLogin = (email, password) => {
  const navigate= useNavigate();
  Auth.signIn(email, password)
        .then(user => {
          // localStorage.setItem(AUTH_USER_TOKEN_KEY, user.signInUserSession.accessToken.jwtToken);
          console.log(JSON.stringify(user));
          console.log("Login Successful");

          
          
        })
        .catch(err => {
          console.log("Login Failed");

          console.log(err);
          
        });
};

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    this.setPassword(event.target.value);
  };
    return (
      <div className="container">
        <h2>Login</h2>
          <div>
            <label htmlFor="email">Email:</label>
            <input type="email" id="email" name="email" onChange={handleEmailChange} required />
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input type="password" id="password" name="password" onChange={handlePasswordChange} required />
          </div>
          <div>
            <input type="submit" value="Login" onClick={() => this.handleLogin(email, password)} />
          </div>
          <div>
            <a href="#">Forgot Username</a>
            <p></p>
            <a href="#">Forgot Password</a>
            <p></p>
            <a href="#">Sign Up</a>
          </div>
      </div>
    );
  }

export default Login;
