import React, { useState } from "react";
import { Auth } from 'aws-amplify';

class Login extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      email: '',
      password: ''
    };
  }

  render() {

    const handleEmailChange = (event) => {
      this.setState({ email: event.target.value });
    };

    const handlePasswordChange = (event) => {
      this.setState({ password: event.target.value });
    };


    return (
      <div className="container">
        <h2>Login</h2>
        <form action="/login" method="POST">
          <div>
            <label htmlFor="email">Email:</label>
            <input type="email" id="email" name="email" onChange={handleEmailChange} required />
          </div>
          <div>
            <label htmlFor="password">Password:</label>
            <input type="password" id="password" name="password" onChange={handlePasswordChange} required />
          </div>
          <div>
            <input type="submit" value="Login" onClick={() => this.handleLogin(email, password)} />
          </div>
          <div>
            <a href="#">Forgot Username</a>
            <p></p>
            <a href="#">Forgot Password</a>
            <p></p>
            <a href="#">Sign Up</a>
          </div>
        </form>
      </div>
    );
  }

  handleLogin = (email, password) => {
    Auth.signIn(email, password)
          .then(user => {
            // localStorage.setItem(AUTH_USER_TOKEN_KEY, user.signInUserSession.accessToken.jwtToken);
            console.log(JSON.stringify(user));
            console.log("Login Successful");
          })
          .catch(err => {
            console.log("Login Failed");

            console.log(err);
          });
  };
}

export default Login;
