import React from "react";
import { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";

const Home = () => {
  const [data, setData] = useState(null);
 const [loading, setLoading] = useState(true);
 const [error, setError] = useState(null);

 useEffect(() => {
  fetchData();
}, []);

const fetchData = async () => {
  try {
    const response = await fetch("https://m1smj96xd3.execute-api.ca-central-1.amazonaws.com/dev/coursea");
    if (!response.ok) {
      throw new Error("Network response was not ok.");
    }
    const data = await response.json();
    console.log(data);
    setData(data);
    setLoading(false);
  } catch (error) {
    console.error("Error fetching data:", error);
    setLoading(false);
  }
};

    return (
      <main>
        <div className="sidebar">
          <ul>
            <li>
              <a href="#">Home</a>
            </li>
            <li>
              <a href="#">My Topics</a>
            </li>
            <li>
              <a href="#">Settings</a>
            </li>
          </ul>
        </div>

        <h2 className="trending-topics">Trending Topics</h2>

        <div className="box-container">
        {data &&
          data['courses'].map(({ id, title }) => (
            <li>
              <h3>{title}</h3>
            </li>
          ))}
          <div className="box">
            <NavLink to ="/coursecontent?title=Cybersecurity" className="box-link">
              <h2>Cybersecurity</h2>

              <div className="image-container">
                <img src="/image1.png" alt="first topic" />
              </div>
              </NavLink>
          </div>

          <div className="box">
            <NavLink to ="/analytics" className="box-link">
              <h2>Analytics</h2>
              <div className="image-container">
                <img src="/image2.png" alt="second topic" />
              </div>
            </NavLink>
          </div>

          <div className="box">
            <NavLink to ="/androiddevelopment" className="box-link">
              <h2>
                <b>Android Development</b>
              </h2>
              <div className="image-container">
                <img src="/image3.png" alt="third topic" />
              </div>
            </NavLink>
          </div>
          <div className="box">
            <NavLink to="/webdevelopment" className="box-link">
              <h2>Web Development</h2>
              <div className="image-container">
                <img src="/image4.png" alt="fourth topic" />
              </div>
            </NavLink>
          </div>
        </div>
      </main>
    );
}

export default Home;
